import { createGlobalStyle } from "styled-components";

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    outline: 0;
  }

  html,
  body,
  #__next {
    height: 100%;
  }

  body {
    text-rendering: optimizeLegibility !important;
    -webkit-font-smoothing: antialiased !important;
    background: #373737;
    color: #fff;
    font-family: "Exo 2", sans-serif;
  }

  button {
    cursor: pointer;
  }

  p {
    font-weight: 200;
    font-size: 22px;
  }

  a {
    text-decoration: none;
    color: blue;
    padding-right: 15px;
    color: #9c9c9c;
    &:hover {
      color: #8a8a8a;
    }
    &:active: {
      color: #777777;
    }
  }


  h1 {
    font-size: 60px;
    font-weight: 100;
  }

  .hero-image {
    border: 10px solid #fff;
  }

  .row {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    width: 100%;
  }

  .image-left {
    flex-direction: row-reverse;
  }

  .column {
    display: flex;
    flex-direction: column;
    flex-basis: 100%;
    margin-right: 15px;
    flex: ${props => (props.cols ? props.cols : 12)}
  }
`;

export default GlobalStyle;
