import Layout from "../../components/Layout";
import fetch from "isomorphic-unfetch";
import ReactHtmlParser from "react-html-parser";
import Link from "next/link";
import _ from "lodash";

import { server } from "../../config";
import { Column } from "./styles";

const AlbumContainer = ({ album }) => (
  <Layout>
    <div className="row">
      <Column col={3}>{ReactHtmlParser(album.content)}</Column>
    </div>

    <Album albumData={album} />
  </Layout>
);

const Album = ({ albumData }) =>
  _albumData.Files.map(photo => (
    <Link key={album.id} href={`/f/${photo.id}`}>
      <Column col={8}>
        <img
          className="hero-image"
          src={photo.url}
          alt={album.content}
          width="100%"
        />
      </Column>
    </Link>
  )).value();

const AlbumItem = ({ albumItem }) =>
  (AlbumContainer.getInitialProps = async function(context) {
    const { id } = context.query;
    const res = await fetch(`${server}/albums/${id}`);
    const albums = await res.json();
    const album = albums[0];
    return { album };
  });

export default AlbumContainer;
