import Layout from "../components/Layout";
import fetch from "isomorphic-unfetch";
import Link from "next/link";

const Albums = ({ albums }) => (
  <>
    <Layout>
      <div className="row">
        <h1>{albums.title}</h1>
        {albums
          ? albums.map(album => (
              <Link key={album.id} href={`/a/${album.id}`}>
                <div className="column col" col={3}>
                  <img
                    className="hero-image"
                    src={`http://localhost:3333/files/${album.Files[0].path}`}
                    alt={album.title}
                    width="100%"
                  />
                  <p>{album.title}</p>
                </div>
              </Link>
            ))
          : null}
      </div>
    </Layout>
  </>
);

Albums.getInitialProps = async function(context) {
  const res = await fetch(`http://localhost:3333/albums`);
  const albums = await res.json();

  return { albums };
};

export default Albums;
