import Layout from '../../components/Layout';
import fetch from 'isomorphic-unfetch';

const Page = ({ page }) => (
  <Layout>
    <div className={`row ${page.classes.row}`} key={page.id}>
      <p className={`${page.classes.col.txt}`}>{page.content}</p>
      <div className={`${page.classes.col.img}`}>
        <img
          className="hero-image"
          src={`//localhost:3333/${page.photos.path}`}
          alt={page.content}
          width="100%"
        />
      </div>
    </div>
  </Layout >
);

Page.getInitialProps = async function (context) {
  const { id } = context.query;
  const res = await fetch(`http://localhost:3333/pages/${id}?_embed=photos`);
  const page = await res.json();
  console.log(page);
  return { page };
};

export default Page;
